import React from "react";
import Card from "./Card";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import AddPlacePopup from "./AddPlacePopup.js";
import EditProfilePopup from "./EditProfilePopup.js";
import EditAvatarPopup from "./EditAvatarPopup.js";
import ImagePopup from "./ImagePopup.js";
import api from "../utils/api.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import { CardsContext } from "../contexts/CardsContext";
import * as auth from "../utils/auth.js";

function AroundUs(props) {
  const [popupState, setPopupState] = React.useState({
    isEditProfilePopupOpen: false,
    isAddPlacePopupOpen: false,
    isEditAvatarPopupOpen: false,
    selectedCard: 0,
  });

  const [cards, setCards] = React.useState([]);

  const [currentUser, setCurrentUser] = React.useState({});

  function handleEditProfileClick() {
    setPopupState({
      isEditProfilePopupOpen: true,
    });
  }
  function handleAddPlaceClick() {
    setPopupState({
      isAddPlacePopupOpen: true,
    });
  }
  function handleEditAvatarClick() {
    setPopupState({
      isEditAvatarPopupOpen: true,
    });
  }
  function handleCardClick(card) {
    setPopupState({
      selectedCard: card,
    });
  }
  function closeAllPopups(evt) {
    setPopupState({
      isEditProfilePopupOpen: false,
      isAddPlacePopupOpen: false,
      isEditAvatarPopupOpen: false,
      selectedCard: undefined,
    });
  }

  function handleUpdateUser({ name, about }) {
    api
      .editUserInfo({ name, about })
      .then((res) => {
        setCurrentUser(res);
      })
      .catch((err) => console.log(err));
    closeAllPopups();
  }

  function handleUpdateAvatar(avatar) {
    api
      .sendUserAvatar(avatar)
      .then((newUser) => {
        setCurrentUser(newUser);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleCardLike(cardProps) {
    const isLiked = cardProps.card.likes.some((i) => i._id === currentUser._id);
    api
      .changeLikeCardStatus(cardProps.card._id, !isLiked)
      .then((newCard) => {
        const newCards = cards.map((c) =>
          c._id === cardProps.card._id ? newCard : c
        );
        setCards(newCards);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleCardDelete(cardProps) {
    api
      .deleteCard(cardProps.card._id)
      .then(() => {
        const newCards = cards.filter((c) => {
          return c._id !== cardProps.card._id;
        });
        setCards(newCards);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleAddPlaceSubmit(cardProps) {
    api
      .postNewCard(cardProps)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((error) => {
        console.error("Error adding a new card:", error);
      });
  }

  function handleUpdateUser(info) {
    api
      .sendUserInfo(info)
      .then((newUser) => {
        setCurrentUser(newUser);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((res) => {
        setCurrentUser(res);
      })
      .then(() => {
        api
          .getInitialCards()
          .then((res) => {
            setCards(result);
          })
          .catch((err) => {
            console.log(err);
          });
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Main
          cards={cards}
          onEditProfileClick={handleEditProfileClick}
          onAddPlaceClick={handleAddPlaceClick}
          onEditAvatarClick={handleEditAvatarClick}
          onCardClick={(card) => handleCardClick(card)}
          onCardLike={(card) => handleCardLike(card)}
          onCardDelete={(card) => handleCardDelete(card)}
        />
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlaceSubmit={handleAddPlaceSubmit}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />
        <PopupWithForm
          name="delete"
          title="¿Estás seguro?"
          buttonValue="Sí"
          onClose={closeAllPopups}
        ></PopupWithForm>
        <ImagePopup
          isOpen={isImagePopupOpen}
          link={selectedCard.link}
          name={selectedCard.name}
          onClose={closeAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
