import { useState, useEffect } from "react";
import Card from "./Card";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import ImagePopup from "./ImagePopup";
import api from "../utils/api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import { CardsContext } from "../contexts/CardsContext";
import * as auth from "../utils/auth.js";

function AroundUs(props) {
  const [popupState, setPopupState] = useState({
    isEditProfilePopupOpen: false,
    isAddPlacePopupOpen: false,
    isEditAvatarPopupOpen: false,
    selectedCard: 0,
  });

  const [currentUser, setCurrentUser] = useState({});

  const [cards, setCards] = useState([]);

  function handleEditAvatarClick() {
    setPopupState({
      isEditAvatarPopupOpen: true,
    });
  }

  function handleEditProfileClick() {
    setPopupState({
      isEditProfilePopupOpen: true,
    });
  }

  function handleAddPlaceClick() {
    setPopupState({
      isAddPlacePopupOpen: true,
    });
  }

  function closeAllPopups() {
    setPopupState({
      isEditProfilePopupOpen: false,
      isAddPlacePopupOpen: false,
      isEditAvatarPopupOpen: false,
      selectedCard: undefined,
    });
  }

  useEffect(() => {
    api
      .getUserInfo()
      .then((res) => {
        setCurrentUser(res);
      })
      .then(() => {
        api
          .getInitialCards()
          .then((res) => {
            setCards(res);
          })
          .catch((err) => {
            console.log(err);
          });
      })
      .catch((err) => {
        console.log(err);
      });

    const jwt = localStorage.getItem("jwt");
    auth
      .getContent(jwt)
      .then((res) => {
        if (res) {
          props.setUserEmail(res.data.email);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="around-us">
      <Header
        redirect="Log out"
        link="/login"
        email={props.email}
        signOut={props.signOut}
      />
      <CurrentUserContext.Provider value={currentUser}>
        <CardsContext.Provider value={{ cards, setCards }}>
          <Main
            onEditProfile={handleEditAvatarClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditProfileClick}
            cards={cards}
            onCardLike={handleCardLike}
          >
            {cards.map((card) => (
              <Card
                key={card._id}
                onCardClick={handleCardClick}
                onCardLike={handleCardLike}
                onCardDelete={handleCardDelete}
                card={card}
              />
            ))}
          </Main>
        </CardsContext.Provider>
        <Footer />
        {/* Edit name & occupation */}
        <EditProfilePopup
          onOpen={popupState.isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
          isPopupOpen={popupState.isEditProfilePopupOpen}
        />
      </CurrentUserContext.Provider>
      {/* Adding new place */}
      <AddPlacePopup
        onOpen={popupState.isAddPlacePopupOpen}
        onClose={closeAllPopups}
        onAddPlace={handleAddPlaceSubmit}
      />
      {/* Open image */}
      <ImagePopup onOpen={popupState.selectedCard} onClose={closeAllPopups} />
      {/* Delete card? */}
      <PopupWithForm
        name="delete"
        title="Are you sure?"
        value="Yes"
        type="popup__fields_type_delete"
      />
      {/* Changing profile picture */}
      <EditAvatarPopup
        onOpen={popupState.isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}
      />
    </div>
  );
}

export default AroundUs;
